// ContactRepository search method
async search(query: string): Promise[Contact[]] {
  return this.repository
    .createQueryBuilder('contact')
    .where(
      {{(
        contact.name % :query OR
        contact.email % :query OR
        contact.company % :query OR
        contact.phone % :query
      )}},
      { query }
    )
    .orderBy('similarity(contact.name, :query)', 'DESC')
    .limit(20)
    .getMany()
}

interface TaskFilters {
  assignee_id?: string
  priority?: TaskPriority
  status?: TaskStatus
  project_id?: string
  overdue?: boolean
  due_before?: Date
  due_after?: Date
}

// Apply filters in repository
async findWithFilters(filters: TaskFilters): Promise[Task[]] {
  const qb = this.repository.createQueryBuilder('task')

  if (filters.assignee_id) {
    qb.andWhere('task.assignee_id = :assignee', { assignee: filters.assignee_id })
  }

  if (filters.overdue) {
    qb.andWhere('task.due_date < NOW() AND task.status != :completed', 
      { completed: TaskStatus.COMPLETED })
  }

  return qb.getMany()
}

