class DocumentService {
  constructor(
    private documentRepository: DocumentRepository,
    private s3Service: S3Service,
    private queueService: QueueService
  ) {}

  async uploadDocument(
    file: Express.Multer.File,
    projectId?: string,
    loanId?: string
  ): Promise[Document] {
    // Validate relationships
    if (!projectId && !loanId) {
      throw new ValidationError('Document must be linked to project or loan')
    }

    // Upload to S3
    const s3Result = await this.s3Service.uploadDocument(file)

    // Create database record
    const document = await this.documentRepository.create({
      name: file.originalname,
      type: this.detectDocumentType(file),
      size: file.size,
      mime_type: file.mimetype,
      storage_key: s3Result.key,
      project_id: projectId,
      loan_id: loanId
    })

    // Queue for AI extraction
    await this.queueForExtraction(document.id, document.type)

    return document
  }

  async getDownloadUrl(documentId: string): Promise[string] {
    const document = await this.documentRepository.findById(documentId)
    if (!document) throw new NotFoundError('Document not found')

    return this.s3Service.getSignedUrl(document.storage_key, 3600)
  }

  private detectDocumentType(file: Express.Multer.File): DocumentType {
    const ext = path.extname(file.originalname).toLowerCase()
    const typeMap = {
      '.pdf': DocumentType.PDF,
      '.doc': DocumentType.WORD,
      '.docx': DocumentType.WORD,
      '.xls': DocumentType.EXCEL,
      '.xlsx': DocumentType.EXCEL,
      '.jpg': DocumentType.IMAGE,
      '.png': DocumentType.IMAGE,
      '.dwg': DocumentType.CAD
    }
    return typeMap[ext] || DocumentType.OTHER
  }
}

class S3Service {
  private s3Client: S3Client
  private bucketName: string

  async uploadDocument(file: Express.Multer.File): Promise[S3UploadResult] {
    const key = this.generateS3Key(file)

    const command = new PutObjectCommand({
      Bucket: this.bucketName,
      Key: key,
      Body: file.buffer,
      ContentType: file.mimetype,
      Metadata: {
        'original-name': file.originalname,
        'upload-timestamp': new Date().toISOString()
      }
    })

    await this.s3Client.send(command)
    return { key, bucket: this.bucketName, url: this.getPublicUrl(key) }
  }

  async getSignedUrl(key: string, expirySeconds: number): Promise[string] {
    const command = new GetObjectCommand({
      Bucket: this.bucketName,
      Key: key
    })

    return getSignedUrl(this.s3Client, command, { expiresIn: expirySeconds })
  }

  private generateS3Key(file: Express.Multer.File): string {
    const date = new Date()
    const year = date.getFullYear()
    const month = String(date.getMonth() + 1).padStart(2, '0')
    const uuid = uuidv4()
    const ext = path.extname(file.originalname)

    return {{documents/${year}/${month}/${uuid}${ext}}}
  }
}

// multer.config.ts
export const uploadConfig = multer({
  storage: multer.memoryStorage(),
  limits: {
    fileSize: 100 _ 1024 _ 1024, // 100MB max
    files: 10 // Max 10 files per request
  },
  fileFilter: (req, file, cb) => {
    const allowedMimes = [
      'application/pdf',
      'image/jpeg',
      'image/png',
      'application/msword',
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
      'application/vnd.ms-excel',
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
    ]

    if (allowedMimes.includes(file.mimetype)) {
      cb(null, true)
    } else {
      cb(new ValidationError({{File type ${file.mimetype} not allowed}}))
    }
  }
})

interface ExtractionJob {
  document_id: string
  document_type: DocumentType
  storage_key: string
  priority: number
}

async queueForExtraction(
  documentId: string,
  documentType: DocumentType
): Promise[void] {
  const job: ExtractionJob = {
    document_id: documentId,
    document_type: documentType,
    storage_key: document.storage_key,
    priority: this.getExtractionPriority(documentType)
  }

  await this.queueService.add('document-extraction', job, {
    delay: 0,
    attempts: 3,
    backoff: { type: 'exponential', delay: 2000 }
  })
}

private getExtractionPriority(type: DocumentType): number {
  // Higher priority for critical documents
  const priorities = {
    [DocumentType.SURVEY]: 10,
    [DocumentType.TITLE_REPORT]: 9,
    [DocumentType.PERMIT]: 8,
    [DocumentType.CONTRACT]: 7,
    [DocumentType.PDF]: 5,
    [DocumentType.IMAGE]: 3,
    [DocumentType.OTHER]: 1
  }
  return priorities[type] || 1
}

